import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddStudent = ({ onStudentAdded }) => {
  const [student, setStudent] = useState({
    name: "",
    age: "",
    studentClass: "",
    phoneNumber: "",
  });
  const navigate = useNavigate(); // For navigation

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent({ ...student, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/api/students", student, {
        headers: { "Content-Type": "application/json" },
      });

      // Show success popup
      if (window.confirm("Student added successfully! Do you want to edit the student details?")) {
        navigate(`/edit/${response.data.id}`); // Navigate to the edit page with the student's ID
      } else {
        navigate("/"); // Navigate back to the home page
      }

      setStudent({ name: "", age: "", studentClass: "", phoneNumber: "" }); // Reset form
      onStudentAdded(response.data);
    } catch (error) {
      const errorMessage = error.response?.data || "Failed to add student.";
      alert(errorMessage);
    }
  };

  return (
    <div>
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={student.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Age:</label>
          <input type="text" name="age" value={student.age} onChange={handleChange} required />
        </div>
        <div>
          <label>Class:</label>
          <input type="text" name="studentClass" value={student.studentClass} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label>
          <input type="number" name="phoneNumber" value={student.phoneNumber} onChange={handleChange} required />
        </div>
        <button type="submit">Add Student</button>
      </form>
    </div>
  );
};

export default AddStudent;
