import React, { useState } from 'react';
import { addStudent } from '../api/studentApi';

const AddStudent = () => {
    const [student, setStudent] = useState({
        name: '',
        age: '',
        class: '',
        phoneNumber: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStudent({
            ...student,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await addStudent(student);
            alert('Student added successfully!');
            setStudent({
                name: '',
                age: '',
                class: '',
                phoneNumber: ''
            });
        } catch (error) {
            alert('Error adding student');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input type="text" name="name" value={student.name} onChange={handleChange} required />
            </div>
            <div>
                <label>Age:</label>
                <input type="text" name="age" value={student.age} onChange={handleChange} required />
            </div>
            <div>
                <label>Class:</label>
                <input type="text" name="class" value={student.class} onChange={handleChange} required />
            </div>
            <div>
                <label>Phone Number:</label>
                <input type="number" name="phoneNumber" value={student.phoneNumber} onChange={handleChange} required />
            </div>
            <button type="submit">Add Student</button>
        </form>
    );
};

export default AddStudent;
