import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getStudents, updateStudent } from '../api/studentApi';

const EditStudent = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [student, setStudent] = useState({
        name: '',
        age: '',
        class: '',
        phoneNumber: ''
    });

    useEffect(() => {
        const fetchStudent = async () => {
            try {
                const response = await getStudents();
                const studentData = response.find(s => s.id === parseInt(id));
                if (studentData) setStudent(studentData);
            } catch (err) {
                console.error(err);
            }
        };
        fetchStudent();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStudent({ ...student, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateStudent(id, student);
            alert("Student updated successfully!");
            navigate('/');
        } catch (error) {
            alert("Error updating student: " + error.message);
        }
    };

    return (
        <div>
            <h2>Edit Student</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={student.name} onChange={handleChange} required />
                </div>
                <div>
                    <label>Age:</label>
                    <input type="text" name="age" value={student.age} onChange={handleChange} required />
                </div>
                <div>
                    <label>Class:</label>
                    <input type="text" name="class" value={student.class} onChange={handleChange} required />
                </div>
                <div>
                    <label>Phone Number:</label>
                    <input type="number" name="phoneNumber" value={student.phoneNumber} onChange={handleChange} required />
                </div>
                <button type="submit">Update Student</button>
            </form>
        </div>
    );
};

export default EditStudent;
