import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { getStudents, updateStudent } from '../api/studentApi';

const EditStudent = () => {
    const { id } = useParams();
    const history = useHistory();
    const [student, setStudent] = useState({
        name: '',
        age: '',
        class: '',
        phoneNumber: ''
    });

    useEffect(() => {
        const fetchStudent = async () => {
            try {
                const studentData = await getStudents(id);
                setStudent(studentData);
            } catch (error) {
                console.error('Error fetching student details:', error);
            }
        };

        fetchStudent();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStudent({ ...student, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateStudent(id, student);
            alert('Student updated successfully!');
            history.push('/');
        } catch (error) {
            alert('Error updating student');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input type="text" name="name" value={student.name} onChange={handleChange} required />
            </div>
            <div>
                <label>Age:</label>
                <input type="text" name="age" value={student.age} onChange={handleChange} required />
            </div>
            <div>
                <label>Class:</label>
                <input type="text" name="class" value={student.class} onChange={handleChange} required />
            </div>
            <div>
                <label>Phone Number:</label>
                <input type="number" name="phoneNumber" value={student.phoneNumber} onChange={handleChange} required />
            </div>
            <button type="submit">Update Student</button>
        </form>
    );
};

export default EditStudent;
