import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles.css";

const AddStudent = () => {
  const navigate = useNavigate();
  const [student, setStudent] = useState({
    name: "",
    age: "",
    studentClass: "",
    phoneNumber: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent({ ...student, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8080/api/students", student, {
        headers: { "Content-Type": "application/json" },
      });
      if (window.confirm("Student added successfully! Do you want to go back to the home page?")) {
        navigate("/");
      }
    } catch (error) {
      alert(error.response?.data || "Failed to add student.");
    }
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Add Student</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label>Name:</label>
          <input type="text" name="name" value={student.name} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Age:</label>
          <input type="text" name="age" value={student.age} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Class:</label>
          <input type="text" name="studentClass" value={student.studentClass} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Phone Number:</label>
          <input type="number" name="phoneNumber" value={student.phoneNumber} onChange={handleChange} required />
        </div>
        <button type="submit" className="form-button">Add Student</button>
      </form>
    </div>
  );
};

export default AddStudent;
