import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const EditStudent = () => {
  const { id } = useParams(); // Get the student ID from the URL
  const navigate = useNavigate();
  const [student, setStudent] = useState({
    name: "",
    age: "",
    studentClass: "",
    phoneNumber: "",
  });

  useEffect(() => {
    // Fetch the student details by ID
    const fetchStudent = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/students/${id}`);
        setStudent(response.data);
      } catch (error) {
        alert("Failed to fetch student details.");
        navigate("/"); // Redirect to home if fetching fails
      }
    };

    fetchStudent();
  }, [id, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent({ ...student, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8080/api/students/${id}`, student, {
        headers: { "Content-Type": "application/json" },
      });
      alert("Student updated successfully!");
      navigate("/"); // Redirect to the home page after updating
    } catch (error) {
      alert("Failed to update student.");
    }
  };

  return (
    <div>
      <h2>Edit Student</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={student.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Age:</label>
          <input type="text" name="age" value={student.age} onChange={handleChange} required />
        </div>
        <div>
          <label>Class:</label>
          <input type="text" name="studentClass" value={student.studentClass} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label>
          <input type="number" name="phoneNumber" value={student.phoneNumber} onChange={handleChange} required />
        </div>
        <button type="submit">Update Student</button>
      </form>
    </div>
  );
};

export default EditStudent;
