import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "../styles.css"; // Import CSS

const EditStudent = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [student, setStudent] = useState({
    name: "",
    age: "",
    studentClass: "",
    phoneNumber: "",
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudent = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/students/${id}`);
        setStudent(response.data);
        setLoading(false);
      } catch (error) {
        alert("Failed to fetch student details.");
        navigate("/");
      }
    };
    fetchStudent();
  }, [id, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent({ ...student, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8080/api/students/${id}`, student, {
        headers: { "Content-Type": "application/json" },
      });
      alert("Student updated successfully!");
      navigate("/");
    } catch (error) {
      alert("Failed to update student.");
    }
  };

  if (loading) {
    return (
      <div className="container">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="form-container">
        <h2>Edit Student</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Name:</label>
            <input type="text" name="name" value={student.name} onChange={handleChange} required />
          </div>
          <div>
            <label>Age:</label>
            <input type="text" name="age" value={student.age} onChange={handleChange} required />
          </div>
          <div>
            <label>Class:</label>
            <input type="text" name="studentClass" value={student.studentClass} onChange={handleChange} required />
          </div>
          <div>
            <label>Phone Number:</label>
            <input
              type="number"
              name="phoneNumber"
              value={student.phoneNumber}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Update Student</button>
        </form>
      </div>
    </div>
  );
};

export default EditStudent;
